CREATE TABLE authorize_net_batch (id BIGINT AUTO_INCREMENT, batch_id VARCHAR(255), settlement_time_utc DATETIME, settlement_time_local DATETIME, settlement_state VARCHAR(255), payment_method VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE authorize_net_transaction (id BIGINT AUTO_INCREMENT, trans_id VARCHAR(255), batch_id VARCHAR(255), submit_time_utc DATETIME, submit_time_local DATETIME, transaction_status VARCHAR(255), first_name VARCHAR(255), last_name VARCHAR(255), account_type VARCHAR(255), account_number VARCHAR(255), settle_amount FLOAT(18, 2), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE paypal_transaction (id BIGINT AUTO_INCREMENT, timestamp DATETIME, timezone VARCHAR(3), type VARCHAR(255), email VARCHAR(255), name VARCHAR(255), transaction_id VARCHAR(255) UNIQUE, status VARCHAR(255), amount FLOAT(18, 2), currency_code VARCHAR(3), fee_amount FLOAT(18, 2), net_amount FLOAT(18, 2), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE variable (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, value VARCHAR(255), PRIMARY KEY(id)) ENGINE = INNODB;
